/* Switch */
.small {

&.iOS7Switch {
  border: 1px solid #b3b3b3;
  border-radius: 19px;
  height: 33px;

  padding: 0px;
  margin: 8px 2px;
  cursor: default; }

.iOS7SwitchOn {
  color: #ffffff;
  background: #4bd762;
  border-color: #4bd762; }

.iOS7SwitchLeft {
  padding: 8px;
  padding-right: 29px;
  text-align: center;
  box-sizing: border-box;
  -moz-box-sizing: border-box;
  -ms-box-sizing: border-box;
  -webkit-box-sizing: border-box; }

.iOS7SwitchRight {
  padding: 8px;
  padding-left: 29px;
  text-align: center;
  box-sizing: border-box;
  -moz-box-sizing: border-box;
  -ms-box-sizing: border-box;
  -webkit-box-sizing: border-box; }

.iOS7SwitchInner {
  margin: 0px 18px;
  margin-right: 15px; }

.iOS7SwitchButton {
  height: 29px;
  width: 29px;
  background: #ffffff;
  border: 1px solid #d9d9d9;
  border-radius: 17px;
  margin-left: -17px;
  box-shadow: 0px 3px 3px #b3b3b3;
  -moz-box-shadow: 0px 3px 3px #b3b3b3;
  -webkit-box-shadow: 0px 3px 3px #b3b3b3; }

.iOS7SwitchOn .iOS7SwitchButton {
  box-shadow: 0px 3px 3px #197027;
  -moz-box-shadow: 0px 3px 3px #197027;
  -webkit-box-shadow: 0px 3px 3px #197027; }

}
